mapper.select:
			SELECT * FROM (SELECT ROWNUM NUM,N.* FROM 
      (SELECT * FROM ORDERINFO ORDER BY O_DATE DESC) N
      WHERE MID =#{param3}
      ) WHERE NUM BETWEEN #{param1} AND #{param2}

		SELECT * FROM (SELECT ROWNUM NUM,N.* FROM 
      (SELECT * FROM INQUIRYBOARD ORDER BY IDATE DESC) N
      WHERE FK_MID =#{param3}
      ) WHERE NUM BETWEEN #{param1} AND #{param2}

SELECT COUNT(*) 
FROM INQUIRYMEMBERS
WHERE MID=#{param1}

SELECT COUNT(*)
 FROM COMMUMEMBERS
 WHERE MID=#{param1}
